const CameraController = ({ targetPosition, setCameraTarget }) => {
  const controlsRef = useRef();
  const isMovingToTarget = useRef(false);
  useFrame(({ camera }) => {
    if (targetPosition && isMovingToTarget.current) {
      const currentPos = new THREE.Vector3(camera.position.x, camera.position.y, camera.position.z);
      const targetPos = new THREE.Vector3(...targetPosition);
      currentPos.lerp(targetPos, 0.05);
      camera.position.set(currentPos.x, currentPos.y, currentPos.z);
      controlsRef.current.target.set(targetPos.x, targetPos.y, targetPos.z);
      controlsRef.current.update();
      if (currentPos.distanceTo(targetPos) < 0.1) {
        isMovingToTarget.current = false;
        setCameraTarget(null);
      }
    }
  });
  useEffect(() => {
    if (targetPosition) {
      isMovingToTarget.current = true;
    }
  }, [targetPosition]);

  return <OrbitControls ref={controlsRef} enableZoom={true} />;
};

